document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".project button");e.forEach(n=>{n.addEventListener("click",function(){const e=n.getAttribute("data-details-id");const t=document.getElementById(e);if(t.style.display==="none"||t.style.display===""){t.style.display="block";n.textContent="Hide Details"}else{t.style.display="none";n.textContent="Show Details"}})});const t=document.getElementById("contactForm");t.addEventListener("submit",function(e){let t=true;document.querySelectorAll(".error").forEach(e=>e.remove());const n=document.getElementById("name");if(n.value.trim().length<2){d(n,"Name must be at least 2 characters long.");t=false}const o=document.getElementById("email");const l=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;if(!l.test(o.value.trim())){d(o,"Please enter a valid email address.");t=false}const a=document.getElementById("message");if(a.value.trim().length<10){d(a,"Message must be at least 10 characters long.");t=false}const s=document.getElementById("phone");const c=/^[0-9\s\(\)-]*$/;if(s.value&&!c.test(s.value.trim())){d(s,"Please enter a valid phone number.");t=false}if(!t){e.preventDefault()}});function d(e,t){const n=document.createElement("div");n.className="error";n.style.color="red";n.style.fontSize="0.9em";n.textContent=t;e.parentNode.insertBefore(n,e.nextSibling)}});const toggleThemeButton=document.getElementById("toggleTheme");toggleThemeButton.addEventListener("click",function(){document.body.classList.toggle("dark-mode");localStorage.setItem("theme",document.body.classList.contains("dark-mode")?"dark":"light")});if(localStorage.getItem("theme")==="dark"){document.body.classList.add("dark-mode")}document.querySelectorAll(".filter-btn").forEach(e=>{e.addEventListener("click",()=>{const t=e.dataset.category;document.querySelectorAll(".project").forEach(e=>{e.style.display=e.dataset.category===t||t==="all"?"block":"none"})})});